mapper.select:
select 
from tb_customer_manage t


        and t.name like concat('%', #{params.name}, '%')


        and t.phone like concat('%', #{params.phone}, '%')


        and t.create_date >= #{params.startDate}


        and t.create_date <= #{params.endDate}


        and t.number_type in

            #{item}



        and t.status in

            #{item}



        and t.manager in

            #{item}



        and t.call_res in

            #{item}



        and t.is_pay = 1


order by t.id, t.phone

select * from ( select t.id, t.name, t.phone, if(left(t.phone, 3) = '+86', substring(t.phone, 4), t.phone) as phonePlu, t.area_num areaNum, t.email email, t.status, t.call_res callRes, t.is_pay isPay, t.manager, t.number_type numberType, t.create_date createDate, t.remark, t.modify_time modifyTime, t.vip_id vipId, t.vip_type vipType, t.passport_id passportId, t.honorific_title honorificTitle, t.birthday, t.nationality, t.province, t.country from tb_customer_manage t where t.id in ( select t1.id from ( select * from ( select IF (LEFT (temp.phone, 3) = '+86', substring(temp.phone, 4), temp.phone) phone, id from tb_customer_manage temp
and temp.name like concat('%', #{params.name}, '%')
and temp.phone like concat('%', #{params.phone}, '%')
and temp.create_date >= #{params.startDate}
and temp.create_date <= #{params.endDate}
and temp.number_type in
#{item}
and temp.status in
#{item}
and temp.manager in
#{item}
) temp_tb where temp_tb.phone in ( SELECT t1.phone FROM ( SELECT IF (LEFT (phone, 3) = '+86', substring(phone, 4), phone) AS phone FROM tb_customer_manage
and name like concat('%', #{params.name}, '%')
and phone like concat('%', #{params.phone}, '%')
and create_date >= #{params.startDate}
and create_date <= #{params.endDate}
and number_type in
#{item}
and status in
#{item}
and manager in
#{item}
) t1 GROUP BY t1.phone HAVING count(t1.phone) > 1 ) ) t1 ) ) tb_dup order by tb_dup.phonePlu

select t.name, t.phone, t.email, case when t.honorific_title = 'MR' then '先生' when t.honorific_title = 'MS' then '小姐' when t.honorific_title = 'MDM' then '女士' else '' end honorificTitle, t.vip_id vipId, ifnull(dic1.dic_val, t.vip_type) vipType, t.passport_id passportId, t.birthday, ifnull(dic3.dic_val, t.nationality) nationality, ifnull(dic2.dic_val, t.province) province, ifnull(dic3.dic_val, t.country) country from tb_customer_manage t left join tb_data_dictionary dic1 on dic1.dic_key = t.vip_type and dic1.dic_type = 'yd_vip_type' left join tb_data_dictionary dic2 on dic2.dic_key = t.province and dic2.dic_type = 'yd_province_data' left join tb_data_dictionary dic3 on dic3.dic_key = t.country and dic3.dic_type = 'yd_nationality_data'
and t.name like concat('%', #{name}, '%')
and t.phone like concat('%', #{phone}, '%')
and t.create_date >= #{startDate}
and t.create_date <= #{endDate}
and t.number_type in
#{item}
and t.status in
#{item}
and t.manager in
#{item}
and t.call_res in
#{item}
and t.is_pay = 1
order by t.id, t.phone
