mapper.update:
update tb_user_income t, (select ifnull(freeze_money, 0) freeze_money from tb_user_income where user_id = #{userId}) temp
set t.freeze_money = (temp.freeze_money + #{freezeMoney})
where t.user_id = #{userId}

update tb_user_income t, (select ifnull(balance, 0) balance from tb_user_income where user_id = #{userId}) temp
set t.balance = (temp.balance + #{balance})
where t.user_id = #{userId}
